export const sortMusic = (fieldName, reverse) => {
    return {
        type: 'SORTMUSIC',
        payload: {fieldName, reverse}
    }
}

export const filterMusic = filters => {
    return {
        type: 'FILTERMUSIC',
        payload: filters
    }
}

export const deleteSong = id => {
    return {
        type: 'DELETESONG',
        payload: id
    }
}

export const addSong = newSong => {
    return {
        type: 'ADDSONG',
        payload: newSong
    }
}
