import React, {useState} from 'react';
import './EnterMusic.css'
import {useDispatch} from 'react-redux';
import {addMusic} from '../../Actions';

const EnterMusic = () => {
    const dispatch = useDispatch()
    
    const [newMusic, setNewMusic] = useState({
        song: '',
        artist: '',
        genre: '',
        rating: '1'
    })

    const handleChange = (e) => {  
        e.preventDefault()
        const {name, value} = e.target
        if (name === 'newMusic'){
            dispatch(addMusic(newMusic))
            return setNewMusic({
                song: '',
                artist: '',
                genre: '',
                rating: '1'
            })
        }
        setNewMusic(Object.assign({...newMusic}, {[name]: value}))
    }

    return (
        <div>
            <h3>Here you can add songs!</h3>
        <form className="enter-music" name='newMusic' onSubmit={handleChange}>
            <input
                type='text'
                name='song'
                value={newMusic.song}
                placeholder='Enter song name'
                onChange={handleChange}/>
            <input
                type='text'
                name='artist'
                value={newMusic.artist}
                placeholder='Artist'
                onChange={handleChange}/>
            <input
                type='text'
                name='genre'
                value={newMusic.genre}
                placeholder='Genre'
                onChange={handleChange}/>
            <label>Rating: </label>
            <select
                name='rating'
                placeholder='Rating'
                value={newMusic.rating}
                onChange={handleChange}>
                    <option className="one-star" value='1'>1</option>
                    <option className="2-star" value='2'>2</option>
                    <option className="3-star" value='3'>3</option>
                    <option className="4-star" value='4'>4</option>
                    <option className="5-star" value='5'>5</option>
            </select>
            <button className='add pointer'>Add song</button>
        </form>
        </div>
    )
}

export default EnterMusic;
